import React from 'react';
import "../../App.css";
import Header from '../header/Header';
import Footer from '../footer/Footer';
import ScrollUp from '../scrollup/ScrollUp';
import "./projectpage.css"

import ReactMarkdown from 'react-markdown'
import remarkMath from 'remark-math'
import rehypeKatex from 'rehype-katex'

import 'katex/dist/katex.min.css' // `rehype-katex` does not import the CSS for you


const SmokingGLM = () => {
    return (
      <>
      <Header />
    
        <div className='container'>

            <section className="projectpage__section">
                    <h2 className="projectpage__title">Smoking - Generalised Linear Models</h2>
                    <span className="projectpage__subtitle">This is the subtitle of the article.</span>
                    <span className="projectpage__stats">

                    <i class="uil uil-calender projectpage__icon"></i>
                    <p>Published on January 17, 2022</p>
                    <p class="projectpage__bullet">•</p>

                    <i class="uil uil-clock projectpage__icon"></i>
                    <p>14 minutes</p>
                    <p class="projectpage__bullet">•</p>

                    <i class="uil uil-book-open projectpage__icon"></i>
                    <p>2925 words</p>

                    </span>

                    <div className='headline__image smoking__image'></div>

                    <p className='project__textblock'><b>Context:</b> As part of my Generalised Linear Models module, I worked in a group of 5 to produce a project analysing the factors that contributed to smoking - using a fictional dataset. <mark className='highlight'><b>We achieved a grade of 95% on this report.</b></mark> All analysis in this project was conducted using R.</p>

                    <ReactMarkdown 
className='ReactMarkdown'
children={`## Abstract`}
remarkPlugins={[remarkMath]}
rehypePlugins={[rehypeKatex]}
/>

<ReactMarkdown 
className='ReactMarkdown'
children={`Abstract-The anti-smoking movement has created a cultural shift in recent decades, resulting in a rise of health concerns and relevant statistical analysis. We aim to answer the question of which path to smoking cessation based on lifestyle choices is the most successful. Throughout this report we have analysed data consisting of 4 categorical variables, and discovered that "years smoked" and 'methods"' are the most impactful in relation to smoking cessation. Furthermore, the best method out of the five options is individual therapy; the only one with a significant confidence interval at the $5 \%$ significance level. Amongst the numerous conclusions that are drawn from this report, the most significant was that an individual is more likely to quit smoking, the shorter the amount of time they've been smoking for, with individuals being $6.45$ times more likely to restart smoking if they've been smoking for 10 years compared to someone whose only been smoking for 4 years. We conclude by outlining which group of people are most at risk of restarting.`}
remarkPlugins={[remarkMath]}
rehypePlugins={[rehypeKatex]}
/>

            </section>

        </div>

      <Footer />
      <ScrollUp />
      </>
    );
  };


export default SmokingGLM
